mapper.select:
SELECT SEAT_STATUS
  FROM SEAT
 WHERE SEAT_NO = #{seatNo}

mapper.update:
UPDATE SEAT
   SET MEMBER_NO = #{memberNo}, SEAT_STATUS = 2, SEAT_DATE = CURRENT_TIMESTAMP()
 WHERE SEAT_NO = #{seatNo}

UPDATE SEAT
   SET SEAT_STATUS = 1, MEMBER_NO = NULL, SEAT_DATE = NULL, SEAT_CODE = NULL
 WHERE SEAT_CODE = #{seatCode}

UPDATE SEAT
   SET SEAT_CODE = #{seatCode}
 WHERE SEAT_NO = #{seatNo}

UPDATE SEAT
   SET SEAT_DATE = CURRENT_TIMESTAMP()
 WHERE SEAT_NO = #{seatNo}
